",["$r","pre",null,{"children":["$r","code",null,{"className":"language-js","children":"import {registerPalette, Infographic} from '@antv/infographic';

// 注册离散色板，即一个颜色字符串数组
registerPalette('discrete-palette', ['#FF356A', '#7BC9FF', '#FFD166']);

// 注册连续色板，即一个返回颜色字符串的函数
registerPalette('continuous-palette', (ratio) => {
  const r = Math.round(255 * ratio);
  const g = Math.round(200 * (1 - ratio));
  const b = 150;
  return `rgb(${r}, ${g}, ${b})`;
});

const infographic = new Infographic({
  // 其他配置项...
});

infographic.render(`
theme
  palette discrete-palette
`);
